const timer = new Timer(),
  interval = new Interval();

$('.add').on('click', function() {
  console.log('add 1 sec');
  interval.add(1);
});

$('.reduce').on('click', function() {
  console.log('reduce 1 sec');
  interval.reduce(1);
});

$('.pause').on('click', function() {
  console.log('pause');
  interval.pause(1000);
});

$('.continue').on('click', function() {
  console.log('continue');
  timer.continue();
});

console.log('start...');

interval.start(1000, (iter) => {
  console.log(iter);
}, 10);